package kr.ac.gwnu.computer;

import java.util.Iterator;

public class App {
    public String getGreeting() {
        return "Hello world.";
    }

    public static void main(String[] args) {
        Major computer = new Major("컴퓨터공학", 3);
        computer.add(new Student("0001", "홍길동"));
        computer.add(new Student("0002", "이몽룡"));
        computer.add(new Student("0003", "심청"));

        for (Student student : computer) {
            System.out.println(student.getName());
        }

        Iterator<Student> studentIterator = computer.iterator();
        while (studentIterator.hasNext()) {
            System.out.println(studentIterator.next().getName());
        }
    }
}
